<?php  include('config.php');?><?php// username and password sent from form//NEVER Remove the mysql_real_escape_string. Else there could be an Sql-Injection!$myusername=mysql_real_escape_string($_POST['myusername']);$mypassword=mysql_real_escape_string($_POST['mypassword']); $sql="SELECT * FROM logon WHERE email='$myusername' and password='$mypassword'";$result=mysql_query($sql);// Replace counting function based on database you are using.$count=mysql_num_rows($result);// If result matched $myusername and $mypassword, table row must be 1 row$group_id=mysql_result($result,0,4);if($count==1){  // Register $myusername, $mypassword and redirect to file "login_success.php"  session_start();  $_SESSION['email']=$myusername;  $_SESSION['group']=$group_id;  //session_destroy();  header("Location: /?login=success");  exit();} else {  echo "Wrong Username or Password";}?>