<?php include('config.php');function get_kits(){	connect();		//$result=mysql_query("SELECT k.sku as ksku, k.description as kdescription, l.lot_number as lot_number, l.date as ldate FROM kits k, kits_lot l WHERE k.sku=l.sku ORDER BY ksku ASC") or die(mysql_error());	$result=mysql_query("SELECT k.sku as ksku, d.description as kdescription, l.lot_number as lot_number, l.date as ldate 			FROM kits k, kits_lot l, kit_description d			WHERE k.sku=l.sku 			AND d.lot_id=l.lot_id			ORDER BY ksku ASC;") or die(mysql_error());	return $result;}function get_kit_description($sku){	connect();		//$result=mysql_query("SELECT * FROM kits WHERE sku='".$sku."'") or die(mysql_error());	$result=mysql_query("SELECT distinct k.kit_id as kit_id, k.sku as sku, d.description as description, c.comp as components, d.temp as temp, d.usages as usages, d.quality as quality						FROM kits k, kit_description d, components c, kits_lot l						WHERE k.sku='".$sku."'						AND k.sku=l.sku						AND l.lot_id=d.lot_id						AND l.lot_id=c.lot_id") or die(mysql_error());	return $result;}function get_lot_description($lot, $sku){	connect();		/*$result=mysql_query("SELECT k.sku as ksku, k.description as kdescription, l.lot_number as lot_number, l.date as ldate, k.temp as ktemp, k.usages as kusage, k.quality as kquality, k.components as kcomponents, s.signer_name as signer, s.signer_signature as signature, s.position as position						FROM kits k, kits_lot l, signer_profile s						WHERE k.sku=l.sku 						AND lot_number='".$lot."' 						AND k.sku='".$sku."'						AND s.signer_name=l.signer") or die(mysql_error());*/	$result=mysql_query("SELECT k.sku as ksku, d.description as kdescription, l.lot_number as lot_number, l.date as ldate, d.temp as ktemp, d.usages as kusage, d.quality as kquality, c.comp as kcomponents, sp.signer_name as signer, sp.signer_signature as signature, sp.position as position			FROM kits k, kit_description d, kits_lot l, components c, signer_profile sp			WHERE k.sku=l.sku			AND l.lot_number='".$lot."' 			AND k.sku='".$sku."'			AND sp.signer_name=l.signer			AND c.lot_id=l.lot_id			AND d.lot_id=l.lot_id") or die(mysql_error());	return $result;}function get_lot_id($sku, $lot){	connect();		$result=mysql_query("SELECT l.lot_id as lot_id		FROM kits_lot l, kits k 		WHERE k.sku=l.sku		AND l.sku='".$sku."'		AND l.lot_number='".$lot."'") or die(mysql_error());	return $result;}function get_sku_specs($sku,$lot){	connect();		$lot_id_result=get_lot_id($sku,$lot);	$lot_id=mysql_result($lot_id_result,0);		$result=mysql_query("SELECT s.spec_id s_id, s.title as stitle, s.description as sdesc, s.ordering as sorder 						FROM kits_spec ks, specs s 						WHERE ks.sku='".$sku."' AND ks.spec_id=s.spec_id 						AND ks.lot_id=".$lot_id."						AND ks.lot_id=s.lot_id						ORDER BY ordering ASC;") or die(mysql_error());	return $result;}?>